/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import React from 'react';

interface Contact {
  id: number;
  name: string;
  phoneNumber: string;
  favorite: boolean;
}

interface ContactListProps {
  contacts: Contact[];
  toggleFavorite: (id: number) => void;
  deleteContact: (id: number) => void;
}

const contactListStyles = css`
  border: 1px solid #ccc;
  padding: 10px;
  margin: 10px;
  background-color: #f0f0f0;
  border-radius: 5px;

  h2 {
    font-size: 20px;
    margin-bottom: 10px;
  }

  div {
    margin: 5px 0;
  }

  button {
    background-color: #0077cc;
    color: #fff;
    border: none;
    padding: 5px 10px;
    border-radius: 3px;
    cursor: pointer;

    &:hover {
      background-color: #005599;
    }
  }
`;

const ContactList: React.FC<ContactListProps> = ({ contacts, toggleFavorite, deleteContact }) => {
  return (
    <div css={contactListStyles}>
      <h2>Contact List</h2>
      {contacts.map((contact) => (
        <div key={contact.id}>
          <p>Name: {contact.name}</p>
          <p>Phone Number: {contact.phoneNumber}</p>
          <p>Favorite: {contact.favorite ? 'Yes' : 'No'}</p>
          <button onClick={() => toggleFavorite(contact.id)}>
            {contact.favorite ? 'Remove Favorite' : 'Add Favorite'}
          </button>
          <button onClick={() => deleteContact(contact.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default ContactList;
